<?php  if(!defined('ABSPATH')){exit;}class IW_ProductReviewIsVerified extends IW_Automation_Condition{function get_title(){return 'If reviewer is a verified customer.';}function allowed_triggers(){return array('IW_ProductReview_Trigger',);}function display_html($config=array()){}function validate_entry($conditions){return true;}function test($config,$trigger){$comment_ID=$trigger->pass_vars[0];return get_comment_meta($comment_ID,'verified',true);}}iw_add_condition_class('IW_ProductReviewIsVerified');